use regex::Regex;
use serde_json::json;
use std::sync::Arc;
use tokio::sync::Mutex;

use crate::auth::AuthService;
use crate::config::{BotPattern, BotPatternRegex};
use crate::models::{TokenType, UserClaims};
use crate::rest::AppState;

#[derive(Debug, Clone)]
pub struct CompiledBotPattern {
    pub id: String,
    pub accepted_patterns: Vec<Regex>,
    pub forbidden_patterns: Vec<Regex>,
    pub categories: Vec<String>,
}

impl CompiledBotPattern {
    pub fn from_bot_pattern(pattern: BotPattern) -> Result<Self, regex::Error> {
        let accepted_patterns = pattern.pattern.accepted
            .into_iter()
            .map(|p| Regex::new(&p))
            .collect::<Result<Vec<_>, _>>()?;

        let forbidden_patterns = pattern.pattern.forbidden
            .into_iter()
            .map(|p| Regex::new(&p))
            .collect::<Result<Vec<_>, _>>()?;

        Ok(Self {
            id: pattern.id,
            accepted_patterns,
            forbidden_patterns,
            categories: pattern.categories,
        })
    }

    pub fn is_match(&self, user_agent: &str) -> bool {
        for accepted_regex in &self.accepted_patterns {
            if accepted_regex.is_match(user_agent) {
                let forbidden_match = self.forbidden_patterns
                    .iter()
                    .any(|forbidden_regex| forbidden_regex.is_match(user_agent));

                return !forbidden_match;
            }
        }
        false
    }
}

pub struct BotDetector {
    patterns: Vec<CompiledBotPattern>,
}

impl BotDetector {
    pub fn new(patterns: Vec<BotPattern>) -> Result<Self, regex::Error> {
        let compiled_patterns = patterns
            .into_iter()
            .map(CompiledBotPattern::from_bot_pattern)
            .collect::<Result<Vec<_>, _>>()?;

        Ok(Self {
            patterns: compiled_patterns,
        })
    }

    pub fn is_bot(&self, user_agent: &str) -> bool {
        self.patterns
            .iter()
            .any(|pattern| pattern.is_match(user_agent))
    }

    pub fn get_bot_categories(&self, user_agent: &str) -> Vec<String> {
        self.patterns
            .iter()
            .find(|pattern| pattern.is_match(user_agent))
            .map(|pattern| pattern.categories.clone())
            .unwrap_or_default()
    }
}"}

