pub async fn bot_detection_middleware(
    State(state): State<AppState>,
    mut request: Request<Body>,
    next: Next,
) -> Result<Response, (StatusCode, Json<serde_json::Value>)> {
    let user_agent = request.headers()
        .get("user-agent")
        .and_then(|h| h.to_str().ok())
        .unwrap_or("");

    let bot_detector = state.bot_detector.lock().await;

    if bot_detector.is_bot(user_agent) {
        let categories = bot_detector.get_bot_categories(user_agent);

        if state.config.bot_detection.log_bot_requests {
            tracing::warn!(
                user_agent = user_agent,
                categories = ?categories,
                "Bot request detected and blocked"
            );
        }

        if state.config.bot_detection.block_bots {
            return Err((StatusCode::FORBIDDEN, Json(json!({
                "error": "Bot access denied",
                "message": "Automated requests are not allowed"
            }))));
        }

        request.extensions_mut().insert("is_bot", true);
        request.extensions_mut().insert("bot_categories", categories);
    }

    Ok(next.run(request).await)
}

