{"content":"import type { CollectionConfig } from 'payload'
import { authenticated } from '../../access/authenticated'

export const UserAchievements: CollectionConfig = {
  slug: 'user-achievements',
  access: {
    admin: authenticated,
    create: authenticated,
    delete: authenticated,
    read: authenticated,
    update: authenticated,
  },
  admin: {
    defaultColumns: ['user', 'achievement', 'unlockedAt'],
    useAsTitle: 'id',
  },
  fields: [
    {
      name: 'user',
      type: 'relationship',
      relationTo: 'users',
      required: true,
      index: true,
    },
    {
      name: 'achievement',
      type: 'relationship',
      relationTo: 'achievements',
      required: true,
      index: true,
    },
    {
      name: 'unlockedAt',
      type: 'date',
      defaultValue: () => new Date(),
      required: true,
    },
    {
      name: 'progress',
      type: 'group',
      fields: [
        {
          name: 'currentValue',
          type: 'number',
          defaultValue: 0,
        },
        {
          name: 'targetValue',
          type: 'number',
        },
        {
          name: 'percentage',
          type: 'number',
          defaultValue: 0,
        },
      ],
    },
  ],
  hooks: {
    afterChange: [
      async ({ doc, req }) => {
        if (!doc.user || !req.payload) return doc

        try {
          const userId = typeof doc.user === 'object' ? doc.user.id : doc.user
          const achievementId = typeof doc.achievement === 'object' ? doc.achievement.id : doc.achievement

          // Get achievement details
          const achievement = await req.payload.findByID({
            collection: 'achievements',
            id: achievementId,
          })

          if (!achievement) return doc

          // Award bonus XP for unlocking achievement
          const user = await req.payload.findByID({
            collection: 'users',
            id: userId,
          })

          if (user) {
            const currentXp = user.gamification?.xp || 0
            const bonusXp = achievement.points || 10

            await req.payload.update({
              collection: 'users',
              id: userId,
              data: {
                gamification: {
                  ...user.gamification,
                  xp: currentXp + bonusXp,
                },
              },
            })
          }

        } catch (error) {
          console.error('Error awarding achievement XP:', error)
        }

        return doc
      },
    ],
  },
}","filePath":"/Users/hassan/Documents/Hassan/KSL-dir/src/collections/UserAchievements/index.ts"}

