{"content":"'use client'

import React, { useState } from 'react'
import { useForm, Controller } from 'react-hook-form'
import { useRouter } from 'next/navigation'
import Link from 'next/link'
import { MagneticButton } from '@/components/ui/MagneticButton'
import { Loader2, AlertCircle, ArrowRight, ArrowLeft, User, Mail, Target, Settings, Sparkles } from 'lucide-react'
import { useAuth } from '@/providers/Auth'

interface RegistrationData {
  name: string
  email: string
  password: string
  confirmPassword: string
  learningGoals: string
  preferredDifficulty: string
  dailyGoal: number
  notifications: {
    email: {
      achievements: boolean
      streakReminders: boolean
      weeklyProgress: boolean
    }
    push: {
      dailyReminders: boolean
      achievementUnlocked: boolean
    }
  }
  privacy: {
    profileVisibility: string
    dataSharing: boolean
  }
}

const steps = [
  { id: 'basic', title: 'Basic Info', icon: User },
  { id: 'goals', title: 'Learning Goals', icon: Target },
  { id: 'preferences', title: 'Preferences', icon: Settings },
]

export function MultiStepRegisterForm() {
  const router = useRouter()
  const { setUser } = useAuth()
  const [currentStep, setCurrentStep] = useState(0)
  const [error, setError] = useState<string | null>(null)
  const [loading, setLoading] = useState(false)
  const [formData, setFormData] = useState<Partial<RegistrationData>>({})

  const { control, handleSubmit, watch, formState: { errors }, trigger } = useForm<RegistrationData>({
    defaultValues: {
      learningGoals: 'casual',
      preferredDifficulty: 'beginner',
      dailyGoal: 5,
      notifications: {
        email: {
          achievements: true,
          streakReminders: true,
          weeklyProgress: true,
        },
        push: {
          dailyReminders: true,
          achievementUnlocked: true,
        },
      },
      privacy: {
        profileVisibility: 'public',
        dataSharing: false,
      },
    },
  })

  const password = watch('password')

  const onNext = async () => {
    const fields = currentStep === 0 ? ['name', 'email', 'password', 'confirmPassword'] : 
                   currentStep === 1 ? ['learningGoals', 'preferredDifficulty', 'dailyGoal'] : []

    const isValid = await trigger(fields as any)
    if (isValid) {
      const currentData = watch()
      setFormData(prev => ({ ...prev, ...currentData }))
      if (currentStep < steps.length - 1) {
        setCurrentStep(currentStep + 1)
      } else {
        await onSubmit({ ...formData, ...currentData } as RegistrationData)
      }
    }
  }

  const onPrevious = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1)
    }
  }

  const onSubmit = async (data: RegistrationData) => {
    if (data.password !== data.confirmPassword) {
      setError("Passwords do not match.")
      return
    }

    setLoading(true)
    setError(null)

    try {
      const res = await fetch('/api/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: data.email,
          password: data.password,
          name: data.name,
          profile: {
            learningGoals: data.learningGoals,
            preferredDifficulty: data.preferredDifficulty,
            dailyGoal: data.dailyGoal,
          },
          notifications: data.notifications,
          privacy: data.privacy,
        }),
      })

      const json = await res.json()

      if (res.ok && json.doc) {
        const loginRes = await fetch('/api/users/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            email: data.email,
            password: data.password,
          }),
        })

        if (loginRes.ok) {
          const loginJson = await loginRes.json()
          setUser(loginJson.user)
          router.push('/profile?welcome=true')
        } else {
          router.push('/login?message=account_created')
        }

        router.refresh()
      } else {
        setError(json.errors?.[0]?.message || 'Registration failed')
      }
    } catch (err) {
      setError('Connection failed. Please try again.')
    } finally {
      setLoading(false)
    }
  }

  const renderStep = () => {
    const StepIcon = steps[currentStep].icon

    switch (currentStep) {
      case 0:
        return (
          <div className="space-y-6">
            <div className="text-center space-y-2">
              <div className="w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <StepIcon className="w-8 h-8 text-primary" />
              </div>
              <h1 className="text-2xl font-bold tracking-tighter flex items-center justify-center gap-2">
                <Sparkles className="w-6 h-6 text-primary" />
                Create Your Identity
              </h1>
              <p className="text-muted-foreground text-sm">Join the KSL neural network</p>
            </div>

            <div className="space-y-4">
              <div className="space-y-2">
                <label className="text-xs font-semibold uppercase tracking-wider text-muted-foreground/80 ml-1">Full Name</label>
                <Controller
                  name="name"
                  control={control}
                  rules={{ required: 'Name is required' }}
                  render={({ field }) => (
                    <input
                      {...field}
                      type="text"
                      placeholder="Jane Doe"
                      className="w-full h-12 px-4 rounded-xl bg-background/50 border border-border focus:border-primary/50 focus:ring-0 focus:bg-background transition-all outline-none md:text-sm"
                    />
                  )}
                />
                {errors.name && <p className="text-xs text-destructive">{errors.name.message}</p>}
              </div>

              <div className="space-y-2">
                <label className="text-xs font-semibold uppercase tracking-wider text-muted-foreground/80 ml-1">Email</label>
                <Controller
                  name="email"
                  control={control}
                  rules={{ 
                    required: 'Email is required',
                    pattern: {
                      value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                      message: 'Invalid email address'
                    }
                  }}
                  render={({ field }) => (
                    <input
                      {...field}
                      type="email"
                      placeholder="operator@ksl.com"
                      className="w-full h-12 px-4 rounded-xl bg-background/50 border border-border focus:border-primary/50 focus:ring-0 focus:bg-background transition-all outline-none md:text-sm"
                    />
                  )}
                />
                {errors.email && <p className="text-xs text-destructive">{errors.email.message}</p>}
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <label className="text-xs font-semibold uppercase tracking-wider text-muted-foreground/80 ml-1">Password</label>
                  <Controller
                    name="password"
                    control={control}
                    rules={{ 
                      required: 'Password is required',
                      minLength: {
                        value: 6,
                        message: 'Password must be at least 6 characters'
                      }
                    }}
                    render={({ field }) => (
                      <input
                        {...field}
                        type="password"
                        placeholder="••••••••"
                        className="w-full h-12 px-4 rounded-xl bg-background/50 border border-border focus:border-primary/50 focus:ring-0 focus:bg-background transition-all outline-none md:text-sm"
                      />
                    )}
                  />
                  {errors.password && <p className="text-xs text-destructive">{errors.password.message}</p>}
                </div>

                <div className="space-y-2">
                  <label className="text-xs font-semibold uppercase tracking-wider text-muted-foreground/80 ml-1">Confirm</label>
                  <Controller
                    name="confirmPassword"
                    control={control}
                    rules={{ 
                      required: 'Please confirm password',
                      validate: value => value === password || 'Passwords do not match'
                    }}
                    render={({ field }) => (
                      <input
                        {...field}
                        type="password"
                        placeholder="••••••••"
                        className="w-full h-12 px-4 rounded-xl bg-background/50 border border-border focus:border-primary/50 focus:ring-0 focus:bg-background transition-all outline-none md:text-sm"
                      />
                    )}
                  />
                  {errors.confirmPassword && <p className="text-xs text-destructive">{errors.confirmPassword.message}</p>}
                </div>
              </div>
            </div>
          </div>
        )

      case 1:
        return (
          <div className="space-y-6">
            <div className="text-center space-y-2">
              <div className="w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <StepIcon className="w-8 h-8 text-primary" />
              </div>
              <h2 className="text-2xl font-bold tracking-tighter">Set Your Learning Goals</h2>
              <p className="text-muted-foreground text-sm">Customize your learning experience</p>
            </div>

            <div className="space-y-6">
              <div className="space-y-2">
                <label className="text-xs font-semibold uppercase tracking-wider text-muted-foreground/80 ml-1">Learning Goals</label>
                <Controller
                  name="learningGoals"
                  control={control}
                  render={({ field }) => (
                    <select
                      {...field}
                      className="w-full h-12 px-4 rounded-xl bg-background/50 border border-border focus:border-primary/50 focus:ring-0 focus:bg-background transition-all outline-none md:text-sm"
                    >
                      <option value="casual">Casual Learner (5 signs/week)</option>
                      <option value="dedicated">Dedicated Student (10 signs/week)</option>
                      <option value="intensive">Intensive Learner (20+ signs/week)</option>
                      <option value="categories">Focus on Categories</option>
                      <option value="conversations">Focus on Conversations</option>
                    </select>
                  )}
                />
              </div>

              <div className="space-y-2">
                <label className="text-xs font-semibold uppercase tracking-wider text-muted-foreground/80 ml-1">Experience Level</label>
                <Controller
                  name="preferredDifficulty"
                  control={control}
                  render={({ field }) => (
                    <select
                      {...field}
                      className="w-full h-12 px-4 rounded-xl bg-background/50 border border-border focus:border-primary/50 focus:ring-0 focus:bg-background transition-all outline-none md:text-sm"
                    >
                      <option value="beginner">Beginner</option>
                      <option value="intermediate">Intermediate</option>
                      <option value="advanced">Advanced</option>
                    </select>
                  )}
                />
              </div>

              <div className="space-y-2">
                <label className="text-xs font-semibold uppercase tracking-wider text-muted-foreground/80 ml-1">Daily Goal</label>
                <Controller
                  name="dailyGoal"
                  control={control}
                  render={({ field }) => (
                    <input
                      {...field}
                      type="number"
                      min="1"
                      max="50"
                      className="w-full h-12 px-4 rounded-xl bg-background/50 border border-border focus:border-primary/50 focus:ring-0 focus:bg-background transition-all outline-none md:text-sm"
                    />
                  )}
                />
              </div>
            </div>
          </div>
        )

      case 2:
        return (
          <div className="space-y-6">
            <div className="text-center space-y-2">
              <div className="w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center mx-auto mb-4">
                <StepIcon className="w-8 h-8 text-primary" />
              </div>
              <h2 className="text-2xl font-bold tracking-tighter">Preferences</h2>
              <p className="text-muted-foreground text-sm">Set your privacy and notification preferences</p>
            </div>

            <div className="space-y-6">
              <div className="space-y-4">
                <h3 className="text-sm font-semibold uppercase tracking-wider text-muted-foreground/80">Profile Privacy</h3>
                <Controller
                  name="privacy.profileVisibility"
                  control={control}
                  render={({ field }) => (
                    <select
                      {...field}
                      className="w-full h-12 px-4 rounded-xl bg-background/50 border border-border focus:border-primary/50 focus:ring-0 focus:bg-background transition-all outline-none md:text-sm"
                    >
                      <option value="public">Public</option>
                      <option value="friends">Friends Only</option>
                      <option value="private">Private</option>
                    </select>
                  )}
                />
              </div>

              <div className="space-y-4">
                <h3 className="text-sm font-semibold uppercase tracking-wider text-muted-foreground/80">Email Notifications</h3>
                <div className="space-y-3">
                  <Controller
                    name="notifications.email.achievements"
                    control={control}
                    render={({ field }) => (
                      <label className="flex items-center space-x-3 cursor-pointer">
                        <input
                          type="checkbox"
                          checked={field.value}
                          onChange={field.onChange}
                          className="w-4 h-4 rounded border-border"
                        />
                        <span className="text-sm">Achievement notifications</span>
                      </label>
                    )}
                  />

                  <Controller
                    name="notifications.email.streakReminders"
                    control={control}
                    render={({ field }) => (
                      <label className="flex items-center space-x-3 cursor-pointer">
                        <input
                          type="checkbox"
                          checked={field.value}
                          onChange={field.onChange}
                          className="w-4 h-4 rounded border-border"
                        />
                        <span className="text-sm">Streak reminders</span>
                      </label>
                    )}
                  />

                  <Controller
                    name="notifications.email.weeklyProgress"
                    control={control}
                    render={({ field }) => (
                      <label className="flex items-center space-x-3 cursor-pointer">
                        <input
                          type="checkbox"
                          checked={field.value}
                          onChange={field.onChange}
                          className="w-4 h-4 rounded border-border"
                        />
                        <span className="text-sm">Weekly progress reports</span>
                      </label>
                    )}
                  />
                </div>
              </div>
            </div>
          </div>
        )

      default:
        return null
    }
  }

  return (
    <div className="space-y-6">
      {/* Progress indicator */}
      <div className="flex items-center justify-center space-x-2">
        {steps.map((step, index) => {
          const StepIcon = step.icon
          return (
            <React.Fragment key={step.id}>
              <div className={`flex items-center justify-center w-10 h-10 rounded-full border-2 transition-all ${
                index <= currentStep 
                  ? 'border-primary bg-primary text-primary-foreground' 
                  : 'border-border bg-background text-muted-foreground'
              }`}>
                <StepIcon className="w-5 h-5" />
              </div>
              {index < steps.length - 1 && (
                <div className={`w-8 h-0.5 transition-all ${
                  index < currentStep ? 'bg-primary' : 'bg-border'
                }`} />
              )}
            </React.Fragment>
          )
        })}
      </div>

      {error && (
        <div className="p-3 bg-destructive/10 border border-destructive/20 rounded-lg text-sm text-destructive flex items-center gap-2 animate-in shake">
          <AlertCircle className="w-4 h-4" />
          {error}
        </div>
      )}

      {renderStep()}

      <div className="flex gap-4">
        {currentStep > 0 && (
          <MagneticButton className="flex-1">
            <button
              type="button"
              onClick={onPrevious}
              className="w-full h-12 bg-background border border-border hover:bg-muted rounded-xl font-medium transition-all active:scale-[0.98] flex items-center justify-center gap-2"
            >
              <ArrowLeft className="w-4 h-4" />
              Previous
            </button>
          </MagneticButton>
        )}

        <MagneticButton className={`${currentStep > 0 ? 'flex-1' : 'w-full'}`}>
          <button
            type="button"
            onClick={onNext}
            disabled={loading}
            className="w-full h-12 bg-primary hover:bg-primary/90 text-white rounded-xl font-medium shadow-lg hover:shadow-primary/25 transition-all active:scale-[0.98] flex items-center justify-center gap-2 disabled:opacity-70"
          >
            {loading ? (
              <Loader2 className="w-5 h-5 animate-spin" />
            ) : (
              <>
                {currentStep === steps.length - 1 ? 'Complete Registration' : 'Next'}
                {currentStep < steps.length - 1 && <ArrowRight className="w-4 h-4" />}
              </>
            )}
          </button>
        </MagneticButton>
      </div>

      <div className="text-center text-sm">
        <span className="text-muted-foreground">Already authorized? </span>
        <Link href="/login" className="text-primary font-semibold hover:underline">
          Access Terminal
        </Link>
      </div>
    </div>
  )
