{"content":"'use client'

import React, { useState, useEffect } from 'react'
import { useAuth } from '@/providers/Auth'
import { Card } from '@/components/ui/card'
import { ProgressRing } from '@/components/ProgressRing'
import { MagneticButton } from '@/components/ui/MagneticButton'
import { 
  Trophy, 
  Star, 
  Lock, 
  Unlock,
  Flame,
  Target,
  BookOpen,
  Users,
  Zap
} from 'lucide-react'

interface Achievement {
  id: string
  name: string
  description: string
  badgeIcon: string
  category: string
  points: number
  unlocked: boolean
  unlockedAt?: string
  progress?: {
    current: number
    target: number
    percentage: number
  }
}

interface AchievementCategory {
  id: string
  name: string
  icon: React.ReactNode
  achievements: Achievement[]
}

export function AchievementSystem() {
  const { user } = useAuth()
  const [achievements, setAchievements] = useState<Achievement[]>([])
  const [categories, setCategories] = useState<AchievementCategory[]>([])
  const [loading, setLoading] = useState(true)
  const [selectedCategory, setSelectedCategory] = useState<string>('all')

  useEffect(() => {
    fetchAchievements()
  }, [user])

  const fetchAchievements = async () => {
    if (!user) return

    try {
      const response = await fetch('/api/achievements')
      if (response.ok) {
        const data = await response.json()
        setAchievements(data.achievements)

        const categoryMap = data.achievements.reduce((acc: Record<string, AchievementCategory>, achievement: Achievement) => {
          if (!acc[achievement.category]) {
            acc[achievement.category] = {
              id: achievement.category,
              name: achievement.category.charAt(0).toUpperCase() + achievement.category.slice(1),
              icon: getCategoryIcon(achievement.category),
              achievements: []
            }
          }
          acc[achievement.category].achievements.push(achievement)
          return acc
        }, {})

        setCategories(Object.values(categoryMap))
      }
    } catch (error) {
      console.error('Failed to fetch achievements:', error)
    } finally {
      setLoading(false)
    }
  }

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'learning':
        return <BookOpen className="w-5 h-5" />
      case 'streaks':
        return <Flame className="w-5 h-5" />
      case 'mastery':
        return <Trophy className="w-5 h-5" />
      case 'social':
        return <Users className="w-5 h-5" />
      default:
        return <Star className="w-5 h-5" />
    }
  }

  const filteredAchievements = selectedCategory === 'all' 
    ? achievements 
    : achievements.filter(a => a.category === selectedCategory)

  const unlockedCount = achievements.filter(a => a.unlocked).length
  const totalCount = achievements.length
  const completionPercentage = totalCount > 0 ? (unlockedCount / totalCount) * 100 : 0

  if (loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-2 border-primary border-t-transparent rounded-full" />
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold mb-4 flex items-center justify-center gap-3">
            <Trophy className="w-10 h-10 text-yellow-500" />
            Achievements
          </h1>
          <p className="text-muted-foreground text-lg mb-6">
            Track your learning milestones and unlock new badges
          </p>

          <div className="max-w-md mx-auto">
            <Card className="p-6">
              <div className="flex items-center justify-between mb-4">
                <div>
                  <div className="text-3xl font-bold">{unlockedCount}/{totalCount}</div>
                  <div className="text-sm text-muted-foreground">Achievements Unlocked</div>
                </div>
                <div className="relative w-20 h-20">
                  <ProgressRing
                    progress={completionPercentage}
                    size={80}
                    strokeWidth={8}
                  />
                  <div className="absolute inset-0 flex items-center justify-center">
                    <span className="text-lg font-bold">{Math.round(completionPercentage)}%</span>
                  </div>
                </div>
              </div>
            </Card>
          </div>
        </div>

        {/* Category Filter */}
        <div className="flex flex-wrap justify-center gap-2 mb-8">
          <MagneticButton>
            <button
              onClick={() => setSelectedCategory('all')}
              className={`px-4 py-2 rounded-lg font-medium transition-all ${
                selectedCategory === 'all'
                  ? 'bg-primary text-white'
                  : 'bg-muted hover:bg-muted/80'
              }`}
            >
              All ({totalCount})
            </button>
          </MagneticButton>

          {categories.map((category) => (
            <MagneticButton key={category.id}>
              <button
                onClick={() => setSelectedCategory(category.id)}
                className={`px-4 py-2 rounded-lg font-medium transition-all flex items-center gap-2 ${
                  selectedCategory === category.id
                    ? 'bg-primary text-white'
                    : 'bg-muted hover:bg-muted/80'
                }`}
              >
                {category.icon}
                {category.name} ({category.achievements.length})
              </button>
            </MagneticButton>
          ))}
        </div>

        {/* Achievement Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
