        {filteredAchievements.length === 0 && (
          <div className="text-center py-12">
            <Lock className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
            <h3 className="text-xl font-semibold mb-2">No achievements yet</h3>
            <p className="text-muted-foreground">Start learning to unlock your first achievement!</p>
          </div>
        )}
      </div>
    </div>
  )
}

function AchievementCard({ achievement }: { achievement: Achievement }) {
  const [showDetails, setShowDetails] = useState(false)

  return (
    <Card className={`p-6 transition-all hover:shadow-lg cursor-pointer ${
      achievement.unlocked 
        ? 'bg-gradient-to-br from-yellow-50 to-orange-50 border-yellow-200' 
        : 'opacity-60'
    }`}>
      <div className="text-center">
        {/* Badge */}
        <div className={`w-20 h-20 mx-auto mb-4 rounded-full flex items-center justify-center text-4xl ${
          achievement.unlocked 
            ? 'bg-gradient-to-br from-yellow-400 to-orange-400' 
            : 'bg-gray-200'
        }`}>
          {achievement.unlocked ? (
            achievement.badgeIcon
          ) : (
            <Lock className="w-8 h-8 text-gray-500" />
          )}
        </div>

        {/* Name */}
        <h3 className={`font-semibold mb-2 ${
          achievement.unlocked ? 'text-foreground' : 'text-muted-foreground'
        }`}>
          {achievement.unlocked ? achievement.name : '???'}
        </h3>

        {/* Description */}
        <p className="text-sm text-muted-foreground mb-3">
          {achievement.unlocked ? achievement.description : 'Keep learning to unlock this achievement'}
        </p>

        {/* Progress Bar */}
        {achievement.progress && !achievement.unlocked && (
          <div className="mb-3">
            <div className="flex justify-between text-xs mb-1">
              <span>Progress</span>
              <span>{achievement.progress.current}/{achievement.progress.target}</span>
            </div>
            <div className="w-full bg-muted rounded-full h-2">
              <div 
                className="bg-primary h-2 rounded-full transition-all"
                style={{ width: `${achievement.progress.percentage}%` }}
              />
            </div>
          </div>
        )}

        {/* Points and Status */}
        <div className="flex items-center justify-between text-sm">
          <div className="flex items-center gap-1">
            <Zap className="w-4 h-4 text-yellow-500" />
            <span className="font-medium">{achievement.points} pts</span>
          </div>

          {achievement.unlocked && achievement.unlockedAt && (
            <div className="text-xs text-muted-foreground">
              {new Date(achievement.unlockedAt).toLocaleDateString()}
            </div>
          )}
        </div>

        {/* Details Toggle */}
        {achievement.unlocked && (
          <MagneticButton>
            <button
              onClick={() => setShowDetails(!showDetails)}
              className="w-full mt-3 px-3 py-1 bg-muted hover:bg-muted/80 rounded text-sm font-medium transition-all"
            >
              {showDetails ? 'Hide' : 'Show'} Details
            </button>
          </MagneticButton>
        )}
      </div>

      {/* Expanded Details */}
      {showDetails && achievement.unlocked && (
        <div className="mt-4 pt-4 border-t text-sm text-muted-foreground">
          <div className="space-y-2">
            <div><strong>Category:</strong> {achievement.category}</div>
            <div><strong>Unlocked:</strong> {achievement.unlockedAt && new Date(achievement.unlockedAt).toLocaleDateString()}</div>
          </div>
        </div>
      )}
    </Card>
  )
}","filePath":"/Users/hassan/Documents/Hassan/KSL-dir/src/components/Achievements/AchievementSystem.tsx"}

