{"content":"import { NextResponse } from 'next/server'
import { getPayload } from 'payload'
import { User } from '@/payload-types'

export async function GET(req: Request) {
  try {
    const payload = await getPayload()

    // Get all achievements
    const achievements = await payload.find({
      collection: 'achievements',
      sort: 'order',
    })

    // Get user achievements if authenticated
    const user = await payload.auth({ req })
    let userAchievements = []

    if (user) {
      userAchievements = await payload.find({
        collection: 'user-achievements',
        where: {
          user: {
            equals: user.id,
          },
        },
        populate: ['achievement'],
      })
    }

    // Merge achievements with user progress
    const mergedAchievements = achievements.docs.map((achievement: any) => {
      const userAchievement = userAchievements.docs.find((ua: any) => 
        ua.achievement?.id === achievement.id
      )

      if (userAchievement) {
        return {
          ...achievement,
          unlocked: true,
          unlockedAt: userAchievement.unlockedAt,
          progress: userAchievement.progress,
        }
      }

      // Calculate progress for non-unlocked achievements
      const progress = calculateProgress(achievement, user as User)

      return {
        ...achievement,
        unlocked: false,
        progress,
      }
    })

    return NextResponse.json({
      achievements: mergedAchievements,
    })
  } catch (error) {
    console.error('Achievements API error:', error)
    return NextResponse.json(
      { error: 'Failed to fetch achievements' },
      { status: 500 }
    )
  }
}

function calculateProgress(achievement: any, user: User | null) {
  if (!user) return null

  const { criteria } = achievement
  const currentValue = getCurrentValue(criteria, user)

  if (currentValue === undefined) return null

  const percentage = Math.min((currentValue / criteria.targetValue) * 100, 100)

  return {
    current: currentValue,
    target: criteria.targetValue,
    percentage,
  }
}

function getCurrentValue(criteria: any, user: User) {
  switch (criteria.type) {
    case 'signsLearned':
      return user.gamification?.xp ? Math.floor(user.gamification.xp / 60) : 0 // Approximate signs learned from XP

    case 'streakDays':
      return user.gamification?.streak || 0

    case 'xpEarned':
      return user.gamification?.xp || 0

    default:
      return 0
  }
}","filePath":"/Users/hassan/Documents/Hassan/KSL-dir/src/app/api/achievements/route.ts"}

