      sort: 'order',
    })

    try {
      const requestHeaders = await headers()
      const { user } = await payload.auth({ headers: requestHeaders })

      let userAchievementsResult: any = null

      if (user) {
        userAchievementsResult = await payload.find({
          collection: 'user-achievements',
          where: {
            user: {
              equals: user.id,
            },
          },
          populate: ['achievement'],
        })
      }

      const mergedAchievements = achievementsResult.docs.map((achievement: any) => {
        const userAchievement = userAchievementsResult?.docs?.find((ua: any) => 
          ua.achievement?.id === achievement.id
        )

        if (userAchievement) {
          return {
            ...achievement,
            unlocked: true,
            unlockedAt: userAchievement.unlockedAt,
            progress: userAchievement.progress,
          }
        }

        const progress = calculateProgress(achievement, user)

        return {
          ...achievement,
          unlocked: false,
          progress,
        }
      })

      return NextResponse.json({
        achievements: mergedAchievements,
      })
    } catch (authError) {
      return NextResponse.json({
        achievements: achievementsResult.docs.map((achievement: any) => ({
          ...achievement,
          unlocked: false,
          progress: null,
        })),
